export class WatermarkBuffer {
width?: number;
height?: number;
length?: number;
format?: VideoPixelFormat;
buffer?: Uint8Array;
}

class WatermarkBuffer implements AgoraSerializable {
  const WatermarkBuffer(
      {this.width, this.height, this.length, this.format, this.buffer});

  @JsonKey(name: 'width')
  final int? width;

  @JsonKey(name: 'height')
  final int? height;

  @JsonKey(name: 'length')
  final int? length;

  @JsonKey(name: 'format')
  final VideoPixelFormat? format;

  @JsonKey(name: 'buffer', ignore: true)
  final Uint8List? buffer;

  factory WatermarkBuffer.fromJson(Map<String, dynamic> json) =>
      _$WatermarkBufferFromJson(json);

  @override
  Map<String, dynamic> toJson() => _$WatermarkBufferToJson(this);
}

public class WatermarkBuffer
{
    public int width;

    public int height;

    public int length;

    public VIDEO_PIXEL_FORMAT format;

    public IntPtr buffer;

    public WatermarkBuffer()
    {
        this.buffer = IntPtr.Zero;
        this.width = 0;
        this.height = 0;
        this.length = 0;
        this.format = VIDEO_PIXEL_FORMAT.VIDEO_PIXEL_I420;
    }

    public WatermarkBuffer(int width, int height, int length, VIDEO_PIXEL_FORMAT format, IntPtr buffer)
    {
        this.width = width;
        this.height = height;
        this.length = length;
        this.format = format;
        this.buffer = buffer;
    }
}
