struct LocalAudioStats {
  int numChannels;
  int sentSampleRate;
  int sentBitrate;
  int internalCodec;
  unsigned short txPacketLossRate;
  int audioDeviceDelay;
  int audioPlayoutDelay;
  int earMonitorDelay;
  int aecEstimatedDelay;
};

public static class LocalAudioStats {
  public int numChannels;
  public int sentSampleRate;
  public int sentBitrate;
  public int internalCodec;
  public int txPacketLossRate;
  public int audioDeviceDelay;
  public int audioPlayoutDelay;
  public int earMonitorDelay;
  public int aecEstimatedDelay;
}

__attribute__((visibility("default"))) @interface AgoraRtcLocalAudioStats : NSObject
