struct SimulcastStreamConfig {
  VideoDimensions dimensions;
  int kBitrate;
  int framerate;
  bool operator==(const SimulcastStreamConfig& rhs) const {
    return dimensions == rhs.dimensions && kBitrate == rhs.kBitrate && framerate == rhs.framerate;
  }
};

public class SimulcastStreamConfig {
  public VideoEncoderConfiguration.VideoDimensions dimensions;
  public int bitrate;
  public int framerate;
}

__attribute__((visibility("default"))) @interface AgoraSimulcastStreamConfig: NSObject
@property (assign, nonatomic) int kBitrate;
@property (assign, nonatomic) int framerate;
@property (assign, nonatomic) CGSize dimensions;
@end

