public static class AgoraFacePositionInfo {
    public int x;
    public int y;
    public int width;
    public int height;
    public int distance;
}

export class AgoraFacePositionInfo {

  public x:number = 0;

  public y:number = 0;

  public width:number = 0 ;

  public height:number = 0;

  public distance:number = -1;
}

