public class ChannelMediaInfo {
  public String channelName = null;
  public String token = null;
  public int uid = 0;
  }

__attribute__((visibility("default"))) @interface AgoraChannelMediaRelayInfo: NSObject

@property (copy, nonatomic) NSString * _Nullable token;
@property (copy, nonatomic) NSString * _Nullable channelName;
@property (assign, nonatomic) NSUInteger uid;
- (instancetype _Nonnull)initWithToken:(NSString *_Nullable)token;
@end

struct ChannelMediaInfo {
	const char* channelName;
	const char* token;
	uid_t uid;
    };

public class ChannelMediaInfo
    {
        public ChannelMediaInfo()
        {
        }

        public ChannelMediaInfo(string channelName, string token, uint uid)
        {
            this.channelName = channelName;
            this.token = token;
            this.uid = uid;

        public string channelName { set; get; }
        public string token { set; get; }
        public uint uid { set; get; }
    }

export class ChannelMediaInfo {
  channelName?: string;
  token?: string;
  uid?: number;
}
