public class ExtensionContext {
  public boolean isValid;
  public int uid;
  public String providerName;
  public String extensionName;

  @CalledByNative
  public ExtensionContext() {
    isValid = false;
    uid = 0;
    providerName = "";
    extensionName = "";
  }

  @CalledByNative
  public ExtensionContext(int uid, String providerName, String extensionName) {
    this.isValid = true;
    this.uid = uid;
    this.providerName = providerName;
    this.extensionName = extensionName;
  }
}

@interface AgoraExtensionContext : NSObject
@property (assign, nonatomic) BOOL isValid;
@property (assign, nonatomic) NSUInteger uid;
@property (copy, nonatomic) NSString * _Nullable providerName;
@property (copy, nonatomic) NSString * _Nullable extensionName;
@end

struct ExtensionContext {
    bool isValid;
    uid_t uid;
    const char *providerName;
    const char *extensionName;
    ExtensionContext():isValid(false), uid(0), providerName(NULL), extensionName(NULL) {}
};

public class ExtensionContext
    {
        public bool isValid;
        public uint uid;
        public string providerName;
        public string extensionName;

        public ExtensionContext()
        {
            this.isValid = false;
            this.uid = 0;
            this.providerName = "";
            this.extensionName = "";
        }

        public ExtensionContext(bool isValid, uint uid, string providerName, string extensionName)
        {
            this.isValid = isValid;
            this.uid = uid;
            this.providerName = providerName;
            this.extensionName = extensionName;
        }
    }

@JsonSerializable(explicitToJson: true, includeIfNull: false)
class ExtensionContext {
  const ExtensionContext(
      {this.isValid, this.uid, this.providerName, this.extensionName});

  @JsonKey(name: 'isValid')
  final bool? isValid;

  @JsonKey(name: 'uid')
  final int? uid;

  @JsonKey(name: 'providerName')
  final String? providerName;

  @JsonKey(name: 'extensionName')
  final String? extensionName;

  factory ExtensionContext.fromJson(Map<String, dynamic> json) =>
      _$ExtensionContextFromJson(json);

  Map<String, dynamic> toJson() => _$ExtensionContextToJson(this);
}
