struct InjectStreamConfig {
    int width;
    int height;
    int videoGop;
    int videoFramerate;
    int videoBitrate;
    AUDIO_SAMPLE_RATE_TYPE audioSampleRate;
    int audioBitrate;
    int audioChannels;

    InjectStreamConfig()
        : width(0)
        , height(0)
        , videoGop(30)
        , videoFramerate(15)
        , videoBitrate(400)
        , audioSampleRate(AUDIO_SAMPLE_RATE_48000)
        , audioBitrate(48)
        , audioChannels(1)
    {}
    };

public class InjectStreamConfig
      {
          public InjectStreamConfig()
          {
              width = 0;
              height = 0;
              videoGop = 30;
              videoFramerate = 15;
              videoBitrate = 400;
              audioSampleRate = AUDIO_SAMPLE_RATE_TYPE.AUDIO_SAMPLE_RATE_48000;
              audioBitrate = 48;
              audioChannels = 1;
          }

          public InjectStreamConfig(int width, int height, int videoGop, int videoFramerate, int videoBitrate,
              AUDIO_SAMPLE_RATE_TYPE audioSampleRate, int audioBitrate, int audioChannels)
          {
              this.width = width;
              this.height = height;
              this.videoGop = videoGop;
              this.videoFramerate = videoFramerate;
              this.videoBitrate = videoBitrate;
              this.audioSampleRate = audioSampleRate;
              this.audioBitrate = audioBitrate;
              this.audioChannels = audioChannels;
          }

          public int width { set; get; }
          public int height { set; get; }
          public int videoGop { set; get; }
          public int videoFramerate { set; get; }
          public int videoBitrate { set; get; }
          public AUDIO_SAMPLE_RATE_TYPE audioSampleRate { set; get; }
          public int audioBitrate { set; get; }
          public int audioChannels { set; get; }
      }

class InjectStreamConfig {
    const InjectStreamConfig(
        {this.width,
        this.height,
        this.videoGop,
        this.videoFramerate,
        this.videoBitrate,
        this.audioSampleRate,
        this.audioBitrate,
        this.audioChannels});

    @JsonKey(name: 'width')
    final int? width;
    @JsonKey(name: 'height')
    final int? height;
    @JsonKey(name: 'videoGop')
    final int? videoGop;
    @JsonKey(name: 'videoFramerate')
    final int? videoFramerate;
    @JsonKey(name: 'videoBitrate')
    final int? videoBitrate;
    @JsonKey(name: 'audioSampleRate')
    final AudioSampleRateType? audioSampleRate;
    @JsonKey(name: 'audioBitrate')
    final int? audioBitrate;
    @JsonKey(name: 'audioChannels')
    final int? audioChannels;
    factory InjectStreamConfig.fromJson(Map<String, dynamic> json) =>
        _$InjectStreamConfigFromJson(json);
    Map<String, dynamic> toJson() => _$InjectStreamConfigToJson(this);
  }
