public class MusicChartInfo {
  public String name;
  public int type;

  @CalledByNative
  public MusicChartInfo(String name, int type) {
    this.name = name;
    this.type = type;
  }

  @CalledByNative
  public String getName() {
    return name;
  }

  @CalledByNative
  public int getType() {
    return type;
  }
}

