__attribute__((visibility("default"))) @interface AgoraMusicCollection : NSObject
@property(nonatomic, assign) NSInteger count;
@property(nonatomic, assign) NSInteger total;
@property(nonatomic, assign) NSInteger page;
@property(nonatomic, assign) NSInteger pageSize;
@property(nonatomic, strong) NSArray<AgoraMusic *>* musicList;
@end

class MusicCollection : public RefCountInterface {
public:
    virtual int getCount() = 0;
    virtual int getTotal() = 0;
    virtual int getPage() = 0;
    virtual int getPageSize() = 0;
    virtual Music* getMusic(int32_t index) = 0;
protected:
    virtual ~MusicCollection() = default;
};

export abstract class MusicCollection {
  abstract getCount(): number;
  abstract getTotal(): number;
  abstract getPage(): number;
  abstract getPageSize(): number;
  abstract getMusic(index: number): Music;
}

public class MusicCollection
    {
        public int count;
        public int total;
        public int page;
        public int pageSize;
        public Music[] music;
    };

export abstract class MusicCollection {
  abstract getCount(): number;
  abstract getTotal(): number;
  abstract getPage(): number;
  abstract getPageSize(): number;
  abstract getMusic(index: number): Music;
}

abstract class MusicCollection {
  int getCount();

  int getTotal();

  int getPage();

  int getPageSize();

  Music getMusic(int index);
}
