public class MusicContentCenterConfiguration {
  public String appId;
  public String token;
  public long mccUid;
  public IMusicContentCenterEventHandler eventHandler;

  public MusicContentCenterConfiguration() {
    this.appId = null;
    this.token = null;
    this.mccUid = 0;
    eventHandler = null;
  }

  public MusicContentCenterConfiguration(
      String appid, String token, long mccUid, IMusicContentCenterEventHandler eventHandler) {
    this.appId = appid;
    this.token = token;
    this.mccUid = mccUid;
    this.eventHandler = eventHandler;
  }

  @CalledByNative
  public String getAppId() {
    return this.appId;
  }

  @CalledByNative
  public String getToken() {
    return this.token;
  }

  @CalledByNative
  public long getMccUid() {
    return this.mccUid;
  }

  @CalledByNative
  public IMusicContentCenterEventHandler getEventHandler() {
    return eventHandler;
  }
}

