public class MusicContentCenterConfiguration {

  public String appId;
  public String token;
  public long mccUid;
  public int maxCacheSize;
  public IMusicContentCenterEventHandler eventHandler;

  public MusicContentCenterConfiguration() {
    this.appId = null;
    this.token = null;
    this.mccUid = 0;
    this.maxCacheSize = 10;
    eventHandler = null;
  }
}

