public static class Rectangle {
    public int x = 0;
    public int y = 0;
    public int width = 0;
    public int height = 0;

    public Rectangle() {
        x = 0;
        y = 0;
        width = 0;
        height = 0;
    }

    public Rectangle(int x_, int y_, int width_, int height_) {
        x = x_;
        y = y_;
        width = width_;
        height = height_;
    }
}

struct Rectangle {
    int x;
    int y;
    int width;
    int height;

    Rectangle(): x(0), y(0), width(0), height(0) {}
    Rectangle(int xx, int yy, int ww, int hh): x(xx), y(yy), width(ww), height(hh) {}
};

