struct SIZE {
  int width;
  int height;
  SIZE() : width(0), height(0) {}
  SIZE(int ww, int hh) : width(ww), height(hh) {}
};

export class Size {
  width?: number
  height?: number
}

public class SIZE
    {
        public int width { set; get; }

        public int height { set; get; }

        public SIZE()
        {
            width = 0;
            height = 0;
        }

        public SIZE(int ww, int hh)
        {
            width = ww;
            height = hh;
        }
    };

export class Size {
  width?: number;
  height?: number;
}

class SIZE {
  const SIZE({this.width, this.height});

  @JsonKey(name: 'width')
  final int? width;
  @JsonKey(name: 'height')
  final int? height;
  factory SIZE.fromJson(Map<String, dynamic> json) => _$SIZEFromJson(json);
  Map<String, dynamic> toJson() => _$SIZEToJson(this);
}
