    zoneSetId = -1;
    position = new float[] {0.0f, 0.0f, 0.0f};
    forward = new float[] {0.0f, 0.0f, 0.0f};
    right = new float[] {0.0f, 0.0f, 0.0f};
    up = new float[] {0.0f, 0.0f, 0.0f};
    forwardLength = 0.0f;
    rightLength = 0.0f;
    upLength = 0.0f;
    audioAttenuation = 0.0f;
  }
