  void* d3d11Texture2d;

  float matrix[16];

  uint8_t* alphaBuffer;

  ALPHA_STITCH_MODE alphaStitchMode;

  void* pixelBuffer;

  IVideoFrameMetaInfo* metaInfo;

  ColorSpace colorSpace;
};

