public class EncryptionConfig {
  public enum EncryptionMode {
    AES_128_XTS(1),
    AES_128_ECB(2),
    AES_256_XTS(3),
    SM4_128_ECB(4),
    AES_128_GCM(5),
    AES_256_GCM(6),
    AES_128_GCM2(7),
    AES_256_GCM2(8),
    MODE_END(9);
    private int value;

    private EncryptionMode(int v) {
      value = v;
    }

    public int getValue() {
      return this.value;
    }
}

__attribute__((visibility("default"))) @interface AgoraEncryptionConfig: NSObject

 @property (assign, nonatomic) AgoraEncryptionMode encryptionMode;
 @property (copy, nonatomic) NSString * _Nullable encryptionKey;
 @property (strong, nonatomic) NSData * _Nullable encryptionKdfSalt;
 @end

export interface EncryptionConfig {
    encryptionMode: ENCRYPTION_MODE;
    encryptionKey: string;
    }

class EncryptionConfig {

  @JsonKey(includeIfNull: false)
  EncryptionMode? encryptionMode;

  @JsonKey(includeIfNull: false)
  String? encryptionKey;

  @JsonKey(includeIfNull: false)
  List<int>? encryptionKdfSalt;

  EncryptionConfig({
    this.encryptionMode,
    this.encryptionKey,
    this.encryptionKdfSalt,
  });

  factory EncryptionConfig.fromJson(Map<String, dynamic> json) =>
      _$EncryptionConfigFromJson(json);

  Map<String, dynamic> toJson() => _$EncryptionConfigToJson(this);
}
