struct RtcEngineContext {
    IRtcEngineEventHandler* eventHandler;
    const char* appId;
    void* context;
    unsigned int areaCode;
    LogConfig logConfig;
    RtcEngineContext()
    :eventHandler(NULL)
    ,appId(NULL)
    ,context(NULL)
    ,areaCode(rtc::AREA_CODE_GLOB)
    {}
    };

public class RtcEngineContext
    {
        public RtcEngineContext(string appId, AREA_CODE areaCode = AREA_CODE.AREA_CODE_GLOB,
            LogConfig logConfig = null)
        {
            this.appId = appId;
            _areaCode = (uint) areaCode;
            this.logConfig = logConfig ?? new LogConfig();
        }

        public string appId { set; get; }

        private uint _areaCode;

        public LogConfig logConfig { set; get; }
        public AREA_CODE areaCode
        {
            get { return (AREA_CODE) _areaCode; }
            set { _areaCode = (uint) areaCode; }
        }
    };

export interface RtcEngineContext {
  appId: string;
  areaCode?: AREA_CODE;
  logConfig?: LogConfig;
}

class RtcEngineContext {

                    String appId;

                    @JsonKey(includeIfNull: false, toJson: _$AreaCodeListToJson)
                    List<AreaCode>? areaCode;

                    @JsonKey(includeIfNull: false)
                    LogConfig? logConfig;

                    RtcEngineContext(
                      this.appId, {
                      this.areaCode,
                      this.logConfig,
                    });

                    factory RtcEngineContext.fromJson(Map<String, dynamic> json) =>
                        _$RtcEngineContextFromJson(json);

                    Map<String, dynamic> toJson() => _$RtcEngineContextToJson(this);

                    static int? _$AreaCodeListToJson(List<AreaCode>? instance) {
                      if (instance == null) return null;
                      var areaCode = 0;
                      for (var element in instance) {
                        areaCode |= AreaCodeConverter(element).value();
                      }
                      return areaCode;
                    }
                  }
