typedef struct RtcImage {
            const char* url;
            int x;
            int y;
            int width;
            int height;
            int zOrder;

            RtcImage() : url(NULL), x(0), y(0), width(0), height(0), zOrder(0) {}
            } RtcImage;

__attribute__((visibility("default"))) @interface AgoraImage : NSObject
            @property(strong, nonatomic) NSURL *_Nonnull url;
            @property(assign, nonatomic) CGRect rect;
            @property (assign, nonatomic) NSInteger zOrder;
            @end

public class AgoraImage {
            public String url;
            public int x;
            public int y;
            public int width;
            public int height;
            public int zOrder;

            @CalledByNative
            public int getWidth() {
            return width;
            }

            @CalledByNative
            public int getX() {
            return x;
            }

            @CalledByNative
            public int getY() {
            return y;
            }

            @CalledByNative
            public int getzOrder() {
            return zOrder;
            }

            @CalledByNative
            public int getHeight() {
            return height;
            }

            @CalledByNative
            public String getUrl() {
            return url;
            }
            }

