typedef struct RtcImage {
    RtcImage() :
       url(NULL),
       x(0),
       y(0),
       width(0),
       height(0)
    {}

    const char* url;
    int x;
    int y;
    int width;
    int height;
    } RtcImage;

class AgoraImage {

  String url;
  @JsonKey(includeIfNull: false)
  int? x;
  @JsonKey(includeIfNull: false)
  int? y;
  int? width;
  int? height;

  AgoraImage(
    this.url, {
    this.x,
    this.y,
    this.width,
    this.height,
  });

  factory AgoraImage.fromJson(Map<String, dynamic> json) =>
      _$AgoraImageFromJson(json);

  Map<String, dynamic> toJson() => _$AgoraImageToJson(this);
}
