export class AudioVolumeInfo {

  uid?: number;

  volume?: number;

  vad?: number;

  voicePitch?: number;
}

class AudioVolumeInfo {
    const AudioVolumeInfo({this.uid, this.volume, this.vad, this.voicePitch});

    @JsonKey(name: 'uid')
    final int? uid;
    @JsonKey(name: 'volume')
    final int? volume;
    @JsonKey(name: 'vad')
    final int? vad;
    @JsonKey(name: 'voicePitch')
    final double? voicePitch;
    factory AudioVolumeInfo.fromJson(Map<String, dynamic> json) =>
        _$AudioVolumeInfoFromJson(json);
    Map<String, dynamic> toJson() => _$AudioVolumeInfoToJson(this);
  }

public class AudioVolumeInfo
    {
        public AudioVolumeInfo()
        {
            uid = 0;
            volume = 0;
            vad = 0;
            voicePitch = 0.0;
        }

        public AudioVolumeInfo(uint uid, uint volume, uint vad, double voicePitch)
        {
            this.uid = uid;
            this.volume = volume;
            this.vad = vad;
            this.voicePitch = voicePitch;
        }

        public uint uid { set; get; }
        public uint volume { set; get; }
        public uint vad { set; get; }
        public double voicePitch { set; get; }
    }
