export class LiveStreamAdvancedFeature {

  featureName?: string;

  opened?: boolean;
}

class LiveStreamAdvancedFeature {
  const LiveStreamAdvancedFeature({this.featureName, this.opened});

  @JsonKey(name: 'featureName')
  final String? featureName;
  @JsonKey(name: 'opened')
  final bool? opened;
  factory LiveStreamAdvancedFeature.fromJson(Map<String, dynamic> json) =>
      _$LiveStreamAdvancedFeatureFromJson(json);
  Map<String, dynamic> toJson() => _$LiveStreamAdvancedFeatureToJson(this);
}

public class LiveStreamAdvancedFeature
    {
        public LiveStreamAdvancedFeature()
        {
            featureName = null;
            opened = false;
        }

        public LiveStreamAdvancedFeature(string feat_name, bool open)
        {
            featureName = feat_name;
            opened = open;
        }
        public string featureName { set; get; }
        public bool opened { set; get; }
    };
