export class MultipathStats {
lanTxBytes?: number;
lanRxBytes?: number;
wifiTxBytes?: number;
wifiRxBytes?: number;
mobileTxBytes?: number;
mobileRxBytes?: number;
activePathNum?: number;
pathStats?: PathStats[];
}

class MultipathStats implements AgoraSerializable {
  const MultipathStats(
      {this.lanTxBytes,
      this.lanRxBytes,
      this.wifiTxBytes,
      this.wifiRxBytes,
      this.mobileTxBytes,
      this.mobileRxBytes,
      this.activePathNum,
      this.pathStats});

  @JsonKey(name: 'lanTxBytes')
  final int? lanTxBytes;

  @JsonKey(name: 'lanRxBytes')
  final int? lanRxBytes;

  @JsonKey(name: 'wifiTxBytes')
  final int? wifiTxBytes;

  @JsonKey(name: 'wifiRxBytes')
  final int? wifiRxBytes;

  @JsonKey(name: 'mobileTxBytes')
  final int? mobileTxBytes;

  @JsonKey(name: 'mobileRxBytes')
  final int? mobileRxBytes;

  @JsonKey(name: 'activePathNum')
  final int? activePathNum;

  @JsonKey(name: 'pathStats')
  final List<PathStats>? pathStats;

  factory MultipathStats.fromJson(Map<String, dynamic> json) =>
      _$MultipathStatsFromJson(json);

  @override
  Map<String, dynamic> toJson() => _$MultipathStatsToJson(this);
}

public class MultipathStats
{
    public uint lanTxBytes;

    public uint lanRxBytes;

    public uint wifiTxBytes;

    public uint wifiRxBytes;

    public uint mobileTxBytes;

    public uint mobileRxBytes;

    public int activePathNum;

    public PathStats[] pathStats;

    public MultipathStats()
    {
        this.lanTxBytes = 0;
        this.lanRxBytes = 0;
        this.wifiTxBytes = 0;
        this.wifiRxBytes = 0;
        this.mobileTxBytes = 0;
        this.mobileRxBytes = 0;
        this.activePathNum = 0;
        this.pathStats = null;
    }

    public MultipathStats(uint lanTxBytes, uint lanRxBytes, uint wifiTxBytes, uint wifiRxBytes, uint mobileTxBytes, uint mobileRxBytes, int activePathNum, PathStats[] pathStats)
    {
        this.lanTxBytes = lanTxBytes;
        this.lanRxBytes = lanRxBytes;
        this.wifiTxBytes = wifiTxBytes;
        this.wifiRxBytes = wifiRxBytes;
        this.mobileTxBytes = mobileTxBytes;
        this.mobileRxBytes = mobileRxBytes;
        this.activePathNum = activePathNum;
        this.pathStats = pathStats;
    }
}
