export class Music {
  songCode?: number;
  name?: string;
  singer?: string;
  poster?: string;
  releaseTime?: string;
  durationS?: number;
  type?: number;
  pitchType?: number;
  lyricCount?: number;
  lyricList?: number[];
  climaxSegmentCount?: number;
  climaxSegmentList?: ClimaxSegment[];
}

class Music {
  const Music(
      {this.songCode,
      this.name,
      this.singer,
      this.poster,
      this.releaseTime,
      this.durationS,
      this.type,
      this.pitchType,
      this.lyricCount,
      this.lyricList,
      this.climaxSegmentCount,
      this.climaxSegmentList});

  @JsonKey(name: 'songCode')
  final int? songCode;

  @JsonKey(name: 'name')
  final String? name;

  @JsonKey(name: 'singer')
  final String? singer;

  @JsonKey(name: 'poster')
  final String? poster;

  @JsonKey(name: 'releaseTime')
  final String? releaseTime;

  @JsonKey(name: 'durationS')
  final int? durationS;

  @JsonKey(name: 'type')
  final int? type;

  @JsonKey(name: 'pitchType')
  final int? pitchType;

  @JsonKey(name: 'lyricCount')
  final int? lyricCount;

  @JsonKey(name: 'lyricList')
  final List<int>? lyricList;

  @JsonKey(name: 'climaxSegmentCount')
  final int? climaxSegmentCount;

  @JsonKey(name: 'climaxSegmentList')
  final List<ClimaxSegment>? climaxSegmentList;

  factory Music.fromJson(Map<String, dynamic> json) => _$MusicFromJson(json);

  Map<String, dynamic> toJson() => _$MusicToJson(this);
}

public class Music
    {
        public Int64 songCode;
        public string name;
        public string singer;
        public string poster;
        public string releaseTime;
        public int durationS;
        public int type;
        public int pitchType;
        public int lyricCount;
        public int[] lyricList;
        public int climaxSegmentCount;
        public ClimaxSegment[] climaxSegmentList;
    }
