export class MusicContentCenterConfiguration {
  appId?: string;
  token?: string;
  mccUid?: number;
  maxCacheSize?: number;
}

@JsonSerializable(explicitToJson: true, includeIfNull: false)
class MusicContentCenterConfiguration {
  const MusicContentCenterConfiguration(
      {this.appId, this.token, this.mccUid, this.maxCacheSize});

  @JsonKey(name: 'appId')
  final String? appId;

  @JsonKey(name: 'token')
  final String? token;

  @JsonKey(name: 'mccUid')
  final int? mccUid;

  @JsonKey(name: 'maxCacheSize')
  final int? maxCacheSize;

  factory MusicContentCenterConfiguration.fromJson(Map<String, dynamic> json) =>
      _$MusicContentCenterConfigurationFromJson(json);

  Map<String, dynamic> toJson() =>
      _$MusicContentCenterConfigurationToJson(this);
}

public class MusicContentCenterConfiguration
    {
        public string appId;
        public string token;
        public UInt64 mccUid;
        public UInt32 maxCacheSize;
    }
