export class PathStats {
type?: MultipathType;
txKBitRate?: number;
rxKBitRate?: number;
}

export class PathStats {
type?: MultipathType;
txKBitRate?: number;
rxKBitRate?: number;
}

class PathStats implements AgoraSerializable {
  const PathStats({this.type, this.txKBitRate, this.rxKBitRate});

  @JsonKey(name: 'type')
  final MultipathType? type;

  @JsonKey(name: 'txKBitRate')
  final int? txKBitRate;

  @JsonKey(name: 'rxKBitRate')
  final int? rxKBitRate;

  factory PathStats.fromJson(Map<String, dynamic> json) =>
      _$PathStatsFromJson(json);

  @override
  Map<String, dynamic> toJson() => _$PathStatsToJson(this);
}

public class PathStats
{
    public MultipathType type;

    public int txKBitRate;

    public int rxKBitRate;

    public PathStats()
    {
        this.type = MultipathType.Unknown;
        this.txKBitRate = 0;
        this.rxKBitRate = 0;
    }

    public PathStats(MultipathType t, int tx, int rx)
    {
        this.type = t;
        this.txKBitRate = tx;
        this.rxKBitRate = rx;
    }

}
