export class RecorderInfo {

  fileName?: string;

  durationMs?: number;

  fileSize?: number;
}

export class RecorderInfo {

  fileName?: string;

  durationMs?: number;

  fileSize?: number;
}

public class RecorderInfo
    {
        public string fileName { set; get; }
        public uint durationMs { set; get; }
        public uint fileSize { set; get; }

        public RecorderInfo()
        {
            fileName = "";
            durationMs = 0;
            fileSize = 0;
        }

        public RecorderInfo(string name, uint dur, uint size)
        {
            fileName = name;
            durationMs = dur;
            fileSize = size;
        }
    };
