export class SegmentationProperty {

  modelType?: SegModelType;

  greenCapacity?: number;

 screenColorType?: ScreenColorType;}

class SegmentationProperty implements AgoraSerializable {
  const SegmentationProperty(
      {this.modelType, this.greenCapacity, this.screenColorType});

  @JsonKey(name: 'modelType')
  final SegModelType? modelType;

  @JsonKey(name: 'greenCapacity')
  final double? greenCapacity;

  @JsonKey(name: 'screenColorType')
  final ScreenColorType? screenColorType;

  factory SegmentationProperty.fromJson(Map<String, dynamic> json) =>
      _$SegmentationPropertyFromJson(json);

  @override
  Map<String, dynamic> toJson() => _$SegmentationPropertyToJson(this);
}

public class SegmentationProperty
    {
        public SEG_MODEL_TYPE modelType;

        public float greenCapacity;

        public SCREEN_COLOR_TYPE screenColorType;

        public SegmentationProperty()
        {
            this.modelType = SEG_MODEL_TYPE.SEG_MODEL_AI;
            this.greenCapacity = 0.5f;
            this.screenColorType = SCREEN_COLOR_TYPE.SCREEN_COLOR_AUTO;
        }

        public SegmentationProperty(SEG_MODEL_TYPE modelType, float greenCapacity, SCREEN_COLOR_TYPE screenColorType)
        {
            this.modelType = modelType;
            this.greenCapacity = greenCapacity;
            this.screenColorType = screenColorType;
        }
    }
