export class VideoLayout {
  channelId?: string;
  uid?: number;
  strUid?: string;
  x?: number;
  y?: number;
  width?: number;
  height?: number;
  videoState?: number;
}

@JsonSerializable(explicitToJson: true, includeIfNull: false)
class VideoLayout {
  const VideoLayout(
      {this.channelId,
      this.uid,
      this.strUid,
      this.x,
      this.y,
      this.width,
      this.height,
      this.videoState});
  @JsonKey(name: 'channelId')
  final String? channelId;
  @JsonKey(name: 'uid')
  final int? uid;
  @JsonKey(name: 'strUid')
  final String? strUid;
  @JsonKey(name: 'x')
  final int? x;
  @JsonKey(name: 'y')
  final int? y;
  @JsonKey(name: 'width')
  final int? width;
  @JsonKey(name: 'height')
  final int? height;
  @JsonKey(name: 'videoState')
  final int? videoState;
  factory VideoLayout.fromJson(Map<String, dynamic> json) =>
      _$VideoLayoutFromJson(json);
  Map<String, dynamic> toJson() => _$VideoLayoutToJson(this);
}

