export class WatermarkConfig {
id?: string;
type?: WatermarkSourceType;
buffer?: WatermarkBuffer;
timestamp?: WatermarkTimestamp;
literal?: WatermarkLiteral;
imageUrl?: string;
options?: WatermarkOptions;
}

class WatermarkConfig implements AgoraSerializable {
  const WatermarkConfig(
      {this.id,
      this.type,
      this.buffer,
      this.timestamp,
      this.literal,
      this.imageUrl,
      this.options});

  @JsonKey(name: 'id')
  final String? id;

  @JsonKey(name: 'type')
  final WatermarkSourceType? type;

  @JsonKey(name: 'buffer')
  final WatermarkBuffer? buffer;

  @JsonKey(name: 'timestamp')
  final WatermarkTimestamp? timestamp;

  @JsonKey(name: 'literal')
  final WatermarkLiteral? literal;

  @JsonKey(name: 'imageUrl')
  final String? imageUrl;

  @JsonKey(name: 'options')
  final WatermarkOptions? options;

  factory WatermarkConfig.fromJson(Map<String, dynamic> json) =>
      _$WatermarkConfigFromJson(json);

  @override
  Map<String, dynamic> toJson() => _$WatermarkConfigToJson(this);
}

public class WatermarkConfig
{
    public string id;

    public WATERMARK_SOURCE_TYPE type;

    public WatermarkBuffer buffer;

    public WatermarkTimestamp timestamp;

    public WatermarkLiteral literal;

    public string imageUrl;

    public WatermarkOptions options;

    public WatermarkConfig()
    {
        this.id = "";
        this.type = WATERMARK_SOURCE_TYPE.IMAGE;
        this.imageUrl = "";
    }

    public WatermarkConfig(string id, WATERMARK_SOURCE_TYPE type, WatermarkBuffer buffer, WatermarkTimestamp timestamp, WatermarkLiteral literal, string imageUrl, WatermarkOptions options)
    {
        this.id = id;
        this.type = type;
        this.buffer = buffer;
        this.timestamp = timestamp;
        this.literal = literal;
        this.imageUrl = imageUrl;
        this.options = options;
    }
}
