export class WatermarkTimestamp {
fontSize?: number;
fontFilePath?: string;
strokeWidth?: number;
format?: string;
}

class WatermarkTimestamp implements AgoraSerializable {
  const WatermarkTimestamp({this.fontSize, this.fontFilePath, this.strokeWidth, this.format});
  final int? fontSize;
  final String? fontFilePath;
  final int? strokeWidth;
  final String? format;
  factory WatermarkTimestamp.fromJson(Map<String, dynamic> json) => _$WatermarkTimestampFromJson(json);
  @override
  Map<String, dynamic> toJson() => _$WatermarkTimestampToJson(this);
}

public class WatermarkTimestamp
{
    public int fontSize;
    public string fontFilePath;
    public int strokeWidth;
    public string format;
    public WatermarkTimestamp()
    {
        this.fontSize = 10;
        this.fontFilePath = "";
        this.strokeWidth = 1;
        this.format = "";
    }
    public WatermarkTimestamp(int fontSize, string fontFilePath, int strokeWidth, string format)
    {
        this.fontSize = fontSize;
        this.fontFilePath = fontFilePath;
        this.strokeWidth = strokeWidth;
        this.format = format;
    }
}
