struct CameraCapturerConfiguration {
#if defined(__ANDROID__) || (defined(__APPLE__) && TARGET_OS_IOS) || defined(__OHOS__)
  Optional<CAMERA_DIRECTION> cameraDirection;
  Optional<CAMERA_FOCAL_LENGTH_TYPE> cameraFocalLengthType;
#else
  Optional<const char *> deviceId;
#endif
#if defined(__ANDROID__)
  Optional<const char *> cameraId;
#endif
  Optional<bool> followEncodeDimensionRatio;
  VideoFormat format;
};

public class CameraCapturerConfiguration {
  public CAMERA_DIRECTION cameraDirection = null;
  public String cameraId = null;
  public CAMERA_FOCAL_LENGTH_TYPE cameraFocalLengthType = null;
  public CaptureFormat captureFormat;
  public Boolean followEncodeDimensionRatio = null;
}
