struct MusicContentCenterConfiguration {
    const char *appId;
    const char *token;
    int64_t mccUid;
    int32_t maxCacheSize;
    const char* mccDomain;
    IMusicContentCenterEventHandler* eventHandler;
};

public class MusicContentCenterConfiguration {
  public String appId;
  public String token;
  public long mccUid;
  public int maxCacheSize;
  public IMusicContentCenterEventHandler eventHandler;
  public String mccDomain;
}
