  @JsonKey(name: 'domainList')
  final List<String>? domainList;

  @JsonKey(name: 'domainListSize')
  final int? domainListSize;

  @JsonKey(name: 'verifyDomainName')
  final String? verifyDomainName;

  @JsonKey(name: 'mode')
  final LocalProxyMode? mode;

