  Optional<bool> publishRhythmPlayerTrack;
  Optional<bool> isInteractiveAudience;

  Optional<video_track_id_t> customVideoTrackId;

  Optional<bool> isAudioFilterable;

  ChannelMediaOptions() {}
  ~ChannelMediaOptions() {}

