__attribute__((visibility("default"))) @interface AgoraLogConfig: NSObject
@property (copy, nonatomic) NSString * _Nullable filePath;
@property (assign, nonatomic) NSInteger fileSizeInKB;
@property (assign, nonatomic) AgoraLogLevel level;
@end

struct LogConfig
{
  const char* filePath;
  uint32_t fileSizeInKB;
  LOG_LEVEL level;
  LogConfig() : filePath(NULL), fileSizeInKB(DEFAULT_LOG_SIZE_IN_KB), level(OPTIONAL_LOG_LEVEL_SPECIFIER LOG_LEVEL_INFO) {}
};

USTRUCT(BlueprintType)
struct FLogConfig {

	GENERATED_BODY()

	UPROPERTY(VisibleAnywhere, BlueprintReadWrite, Category = "Agora|LogConfig")
	FString filePath;
	UPROPERTY(VisibleAnywhere, BlueprintReadWrite, Category = "Agora|LogConfig")
	int64 fileSizeInKB = 1024;
	UPROPERTY(VisibleAnywhere, BlueprintReadWrite, Category = "Agora|LogConfig")
	TEnumAsByte<ELOG_LEVEL>	level = ELOG_LEVEL::LOG_LEVEL_INFO;
};

export class LogConfig {

  filePath?: string;

  fileSizeInKB?: number;

  level?: LogLevel;
}

public class LogConfig
    {
        public LogConfig()
        {
            filePath = "";
            fileSizeInKB = 0;
            level = LOG_LEVEL.LOG_LEVEL_INFO;
        }

        public LogConfig(string filePath, uint fileSize = 1024, LOG_LEVEL level = LOG_LEVEL.LOG_LEVEL_INFO)
        {
            this.filePath = filePath;
            this.fileSizeInKB = 0;
            this.level = level;
        }
        public string filePath { set; get; }
        public uint fileSizeInKB { set; get; }
        public LOG_LEVEL level { set; get; }
    };

export class LogConfig {

  filePath?: string;

  fileSizeInKB?: number;

  level?: LogLevel;
}

class LogConfig {
  const LogConfig({this.filePath, this.fileSizeInKB, this.level});

  @JsonKey(name: 'filePath')
  final String? filePath;
  @JsonKey(name: 'fileSizeInKB')
  final int? fileSizeInKB;
  @JsonKey(name: 'level')
  final LogLevel? level;
  factory LogConfig.fromJson(Map<String, dynamic> json) =>
      _$LogConfigFromJson(json);
  Map<String, dynamic> toJson() => _$LogConfigToJson(this);
}
