p.codeblock:
struct Packet {
                    const unsigned char* buffer;
                    unsigned int size;

                    Packet() : buffer(NULL), size(0) {}
                    };

export class Packet {
  buffer?: Uint8Array
  size?: number
}

public class Packet
    {
        public Packet()
        {
            buffer = new byte[0];
        }
        public Packet(byte[] buffer, uint size)
        {
            this.buffer = buffer;
            this.size = size;
        }

        public byte[] buffer { set; get; }

        public uint size { set; get; }
    }

class Packet {
  const Packet({this.buffer, this.size});

  @JsonKey(name: 'buffer', ignore: true)
  final Uint8List? buffer;
  @JsonKey(name: 'size')
  final int? size;
  factory Packet.fromJson(Map<String, dynamic> json) => _$PacketFromJson(json);
  Map<String, dynamic> toJson() => _$PacketToJson(this);
}
