  final String? deviceName;
  factory VideoDeviceInfo.fromJson(Map<String, dynamic> json) =>
      _$VideoDeviceInfoFromJson(json);
  Map<String, dynamic> toJson() => _$VideoDeviceInfoToJson(this);
