static public class VideoDimensions {
  public int width;
  public int height;

  public VideoDimensions(int width, int height) {
    this.width = width;
    this.height = height;
  }

  public VideoDimensions() {
    this.width = 0;
    this.height = 0;
  }
}

struct VideoDimensions {
  int width;
  int height;
  VideoDimensions() : width(640), height(480) {}
  VideoDimensions(int w, int h) : width(w), height(h) {}
  bool operator==(const VideoDimensions& rhs) const {
    return width == rhs.width && height == rhs.height;
  }
};

export interface VideoDimensions {
    width: number,
    height: number
    }

class VideoDimensions {

  @JsonKey(includeIfNull: false)
  int? width;

  @JsonKey(includeIfNull: false)
  int? height;

  VideoDimensions({
    this.width,
    this.height,
  });

  factory VideoDimensions.fromJson(Map<String, dynamic> json) =>
      _$VideoDimensionsFromJson(json);

  Map<String, dynamic> toJson() => _$VideoDimensionsToJson(this);
}
