struct WatermarkRatio {
  float xRatio;
  float yRatio;
  float widthRatio;
  WatermarkRatio() : xRatio(0.0), yRatio(0.0), widthRatio(0.0) {}
  WatermarkRatio(float x, float y, float width) : xRatio(x), yRatio(y), widthRatio(width) {}
};

export class WatermarkRatio {
  xRatio?: number
  yRatio?: number
  widthRatio?: number
}

public class WatermarkRatio
    {
        public WatermarkRatio()
        {
            xRatio = 0.0f;
            yRatio = 0.0f;
            widthRatio = 0.0f;
        }

        public WatermarkRatio(float x, float y, float width)
        {
            xRatio = x;
            yRatio = y;
            widthRatio = width;
        }
        public float xRatio { set; get; }
        public float yRatio { set; get; }
        public float widthRatio { set; get; }
    }

export class WatermarkRatio {
  xRatio?: number;
  yRatio?: number;
  widthRatio?: number;
}

class WatermarkRatio {
  const WatermarkRatio({this.xRatio, this.yRatio, this.widthRatio});

  @JsonKey(name: 'xRatio')
  final double? xRatio;
  @JsonKey(name: 'yRatio')
  final double? yRatio;
  @JsonKey(name: 'widthRatio')
  final double? widthRatio;
  factory WatermarkRatio.fromJson(Map<String, dynamic> json) =>
      _$WatermarkRatioFromJson(json);
