public class AgoraRhythmPlayerConfig  {

    public AgoraRhythmPlayerConfig() {
    beatsPerMeasure = 4;
    beatsPerMinute = 60;
    publish = true;
    }
    public int beatsPerMeasure;
    public int beatsPerMinute;
    public boolean publish;
    }

__attribute__((visibility("default"))) @interface AgoraRtcRhythmPlayerConfig : NSObject
    @property(assign, nonatomic) NSUInteger beatsPerMeasure;
    @property(assign, nonatomic) NSUInteger beatsPerMinute;
    @property(assign, nonatomic) BOOL publish;
    @end

class RhythmPlayerConfig {

  int? beatsPerMeasure;
  int? beatsPerMinute;
  bool? publish;

  RhythmPlayerConfig({
    this.beatsPerMeasure,
    this.beatsPerMinute,
    this.publish,
  });
}
