     encodedFrameHeight: number;
     encodedFrameCount: number;
     codecType: VIDEO_CODEC_TYPE;
     txPacketLossRate: number;
     captureFrameRate: number;   
     captureBrightnessLevel: CAPTURE_BRIGHTNESS_LEVEL_TYPE;
     }
