public static class LogConfig {
    public String filePath;
    public int fileSizeInKB;
    public int level = Constants.LogLevel.getValue(Constants.LogLevel.LOG_LEVEL_INFO);
  }

__attribute__((visibility("default"))) @interface AgoraLogConfig: NSObject
@property (copy, nonatomic) NSString * _Nullable filePath;
@property (assign, nonatomic) NSInteger fileSizeInKB;
@property (assign, nonatomic) AgoraLogLevel level;
@end

struct LogConfig {
    const char* filePath;
    int fileSize;
    LOG_LEVEL level;
    LogConfig()
    :filePath(NULL)
    ,fileSize(-1)
    ,level(LOG_LEVEL::LOG_LEVEL_INFO)
    {}
    };

public class LogConfig
    {
        public LogConfig()
        {
            filePath = null;
            fileSize = -1;
            level = LOG_LEVEL.LOG_LEVEL_INFO;
        }

        public LogConfig(string filePath, int fileSize = 1024, LOG_LEVEL level = LOG_LEVEL.LOG_LEVEL_INFO)
        {
            this.filePath = filePath;
            this.fileSize = fileSize;
            this.level = level;
        }

        public string filePath { set; get; }
        public int fileSize { set; get; }
        public LOG_LEVEL level { set; get; }
    };

export interface LogConfig {
    filePath: string,
    fileSize: number,
    level: LOG_LEVEL
    }

class LogConfig {
  @JsonKey(includeIfNull: false)
  String? filePath;

  @JsonKey(includeIfNull: false)
  int? fileSize;

  @JsonKey(includeIfNull: false)
  LogLevel? level;

  LogConfig({
    this.filePath,
    this.fileSize,
    this.level,
  });

  factory LogConfig.fromJson(Map<String, dynamic> json) =>
      _$LogConfigFromJson(json);

  Map<String, dynamic> toJson() => _$LogConfigToJson(this);
}
