p.codeblock:
struct Packet {
                    const unsigned char* buffer;
                    unsigned int size;

                    Packet() : buffer(NULL), size(0) {}
                    };

public class Packet
    {
        public Packet()
        {
            buffer = new byte[0];
        }
        public Packet(byte[] buffer, uint size)
        {
            this.buffer = buffer;
            this.size = size;
        }

        public byte[] buffer { set; get; }
        public uint size { set; get; }
    }

struct Packet {
	const unsigned char* buffer;
	unsigned int size;
	};

public struct Packet
	{
		public IntPtr buffer;
		public IntPtr size;
	};
