public class RemoteVoicePositionInfo {
  public float[] position;
  public float[] forward;

  public RemoteVoicePositionInfo() {
    position = new float[] {0.0f, 0.0f, 0.0f};
    forward = new float[] {0.0f, 0.0f, 0.0f};
  }
}

__attribute__((visibility("default"))) @interface AgoraRemoteVoicePositionInfo : NSObject
@property(strong, nonatomic) NSArray<NSNumber*> * _Nonnull position;
@property(strong, nonatomic) NSArray<NSNumber*> * _Nullable forward;
@end

struct RemoteVoicePositionInfo {
  float position[3];
  float forward[3];

  RemoteVoicePositionInfo() = default;
};

public class RemoteVoicePositionInfo
    {
        public RemoteVoicePositionInfo(float[] position, float[] forward)
        {
            this.position= position;
            this.forward = forward;
        }

        public float[] position { set; get; }
        public float[] forward { set; get; }
    };
