struct RtcEngineContext {
    IRtcEngineEventHandler* eventHandler;
    const char* appId;
    void* context;
    unsigned int areaCode;
    LogConfig logConfig;
    RtcEngineContext()
    :eventHandler(NULL)
    ,appId(NULL)
    ,context(NULL)
    ,areaCode(rtc::AREA_CODE_GLOB)
    {}
    };

export interface RtcEngineContext {
  appId: string;
  areaCode?: AREA_CODE;
  logConfig?: LogConfig;
}

class RtcEngineContext {

  String appId;
  List<AreaCode>? areaCode;
  LogConfig? logConfig;

  RtcEngineContext(
    this.appId, {
    this.areaCode,
    this.logConfig,
  });
}
