public class ScreenCaptureParameters {
  private VideoDimensions videoDimensions;
  private int frameRate;
  private int bitrate;
}

__attribute__((visibility("default"))) @interface AgoraScreenCaptureParameters: NSObject

@property (assign, nonatomic) CGSize dimensions;
@property (assign, nonatomic) NSInteger frameRate;
@property (assign, nonatomic) NSInteger bitrate;

@end

struct ScreenCaptureParameters {
    VideoDimensions dimensions;
    int frameRate;
    int bitrate;
    bool captureMouseCursor;
    bool windowFocus;
    view_t* excludeWindowList;
    int excludeWindowCount;
};

export interface ScreenCaptureParameters {
    width: number;
    height: number;
    frameRate: number;
    bitrate: number;
    captureMouseCursor: boolean;
    windowFocus: boolean;
    excludeWindowList: Array<number>;
    excludeWindowCount: number;
    }
