p.codeblock:
struct RteFileInfo {
  int32_t file_id;  
  std::string
      file_url;      
  int64_t duration;  
  int32_t video_width;   
  int32_t video_height;  
  int32_t frame_rate;    
  int32_t
      index;  
  int64_t begin_time;           

  RteFileInfo() {
    file_id = INVALID_RTE_FILE_ID;
    file_url = "";
    duration = 0;
    video_width = 0;
    video_height = 0;
    frame_rate = 0;
    index = -1;
    begin_time = 0;
  }

  void Reset() {
    file_id = INVALID_RTE_FILE_ID;
    file_url = "";
    duration = 0;
    video_width = 0;
    video_height = 0;
    frame_rate = 0;
    index = -1;
    begin_time = 0;
  }

  bool IsValid() { return (file_id != INVALID_RTE_FILE_ID); }

  void CloneTo(RteFileInfo& out_file_info) {
    out_file_info.file_id = file_id;
    out_file_info.file_url = file_url;
    out_file_info.video_width = video_width;
    out_file_info.video_height = video_height;
    out_file_info.frame_rate = frame_rate;
    out_file_info.index = index;
    out_file_info.begin_time = begin_time;
  }
};
